// 在U8g2中显示汉字
void U8g2_ShowChinese(u8g2_t *u8g2, uint8_t x, uint8_t y, uint8_t num)
{
    uint8_t i, j;
    uint8_t byte1, byte2;
    
    for(i = 0; i < 16; i++) {  // 16行
        byte1 = Hzk1[num][i * 2];      // 高字节（左8列）
        byte2 = Hzk1[num][i * 2 + 1];  // 低字节（右8列）
        
        for(j = 0; j < 8; j++) {  // 处理左8列
            if(byte1 & (0x80 >> j)) {  // 从高位到低位
                u8g2_DrawPixel(u8g2, x + j, y + i);
            }
        }
        
        for(j = 0; j < 8; j++) {  // 处理右8列
            if(byte2 & (0x80 >> j)) {
                u8g2_DrawPixel(u8g2, x + 8 + j, y + i);
            }
        }
    }
}

// 显示学号和昵称
void testDrawInfo(u8g2_t *u8g2)
{
    int t = 2000;
    u8g2_ClearBuffer(u8g2);
    
    // 显示英文信息
    u8g2_SetFont(u8g2, u8g2_font_ncenB08_tf);
    u8g2_DrawStr(u8g2, 10, 15, "Name:");
    u8g2_DrawStr(u8g2, 10, 45, "ID:123456789123");
    
    // 显示中文"伍丹妮"
    U8g2_ShowChinese(u8g2, 50, 20, 0);  // 伍
    U8g2_ShowChinese(u8g2, 66, 20, 1);  // 丹
    U8g2_ShowChinese(u8g2, 82, 20, 2);  // 妮
    
    SEND_BUFFER_DISPLAY_MS(u8g2, t);
}

// 滑动显示测试（1:左右滑动 2:上下滑动）
void testSlideDisplay(u8g2_t *u8g2, uint8_t slide_type)
{
    int t = 30;  // 滑动步长间隔（越小越流畅）
    char slide_text[] = "U8g2 Slide Test!";  // 滑动显示的文本
    
    if (slide_type == 1)  // 1. 左右滑动（从右向左进入，再从左向右退出）
    {
        // 阶段1：文本从屏幕右侧滑入中间
        for (int x = 128; x >= 20; x -= 2)
        {
            u8g2_ClearBuffer(u8g2);
            u8g2_SetFont(u8g2, u8g2_font_ncenB10_tf);
            u8g2_DrawStr(u8g2, x, 32, slide_text);  // Y固定，X变化
            u8g2_SendBuffer(u8g2);
            HAL_Delay(t);
        }
        HAL_Delay(1000);  // 中间停留1秒
        
        // 阶段2：文本从中间滑出屏幕左侧
        for (int x = 20; x >= -128; x -= 2)
        {
            u8g2_ClearBuffer(u8g2);
            u8g2_DrawStr(u8g2, x, 32, slide_text);
            u8g2_SendBuffer(u8g2);
            HAL_Delay(t);
        }
    }
    else if (slide_type == 2)  // 2. 上下滑动（从下向上进入，再从上向下退出）
    {
        // 阶段1：文本从屏幕下方滑入中间
        for (int y = 64; y >= 32; y -= 2)
        {
            u8g2_ClearBuffer(u8g2);
            u8g2_SetFont(u8g2, u8g2_font_ncenB10_tf);
            u8g2_DrawStr(u8g2, 0, y, slide_text);  // X固定，Y变化
            u8g2_SendBuffer(u8g2);
            HAL_Delay(t);
        }
        HAL_Delay(1000);  // 中间停留1秒
        
        // 阶段2：文本从中间滑出屏幕上方
        for (int y = 32; y >= -20; y -= 2)
        {
            u8g2_ClearBuffer(u8g2);
            u8g2_DrawStr(u8g2, 0, y, slide_text);
            u8g2_SendBuffer(u8g2);
            HAL_Delay(t);
        }
    }
}
 
// 显示自定义图案
void testDrawCustomPattern(u8g2_t *u8g2)
{
    int t = 2000;  // 显示停留时间（2秒）
    u8g2_ClearBuffer(u8g2);
    u8g2_SetFont(u8g2, u8g2_font_ncenB08_tf);
    u8g2_DrawStr(u8g2, 0, 12, "Custom Pattern");  // 标题
    
    // 1. 绘制（坐标：中心X=40, Y=35）
    u8g2_DrawDisc(u8g2, 30, 30, 8, U8G2_DRAW_ALL);  // 左半圆
    u8g2_DrawDisc(u8g2, 50, 30, 8, U8G2_DRAW_ALL);  // 右半圆
    u8g2_DrawLine(u8g2, 22, 30, 40, 45);            // 左下方直线
    u8g2_DrawLine(u8g2, 58, 30, 40, 45);            // 右下方直线
    
    // 2. 绘制（坐标：中心X=90, Y=35）
    u8g2_DrawLine(u8g2, 90, 20, 98, 38);  // 上→右下
    u8g2_DrawLine(u8g2, 98, 38, 82, 28);  // 右下→左中
    u8g2_DrawLine(u8g2, 82, 28, 100, 28); // 左中→右中
    u8g2_DrawLine(u8g2, 100, 28, 84, 38); // 右中→左下
    u8g2_DrawLine(u8g2, 84, 38, 90, 20);  // 左下→上
    
    SEND_BUFFER_DISPLAY_MS(u8g2, t);  // 刷新显示并停留
}

